#!/bin/bash #require sudo if [[ $(/usr/bin/id -u) -ne 0 ]]; then echo "Run script with root privelleges" exit fi #saving input variablesecho "To submit input, press [ENTER] " echo "What is your project name:" read projectnameecho "Project name set to:"$projectnameecho "What is the username for the database:"read db_user echo "username for the database set to:"$db_userecho "What is your password for the database:"read db_pass#saving input variables#before setup echo "Starting apt update"apt update  echo "apt update finished"sleep 2echo "Starting apt upgrade"apt upgrade -y echo "apt upgrade finished"sleep 2#dependencies echo "Starting certificates install"apt install ca-certificates apt-transport-https -y echo "Starting certificates install"echo "Starting apache2 install"apt install apache2 php-mysql libapache2-mod-php7.3 -y echo "apache2 install finished"echo "Starting php install"apt install php-common php-mbstring php-xml php-zip php-curl -y echo "php install finished"echo "Starting mariadb-server install"apt install mariadb-server-10.3 -y echo "mariadb-server install finished"#configure 